"""Main package initialization for Maya Menu Bar."""
import sys
import os

print("Python sys.path:")
for p in sys.path:
    print(p)

print("\nPackage location:", os.path.dirname(os.path.abspath(__file__)))

try:
    from . import compat
    from . import ui
    print("Successfully imported compat and ui modules")
except ImportError as e:
    import traceback
    traceback.print_exc()
    print("Available files in package:", os.listdir(os.path.dirname(os.path.abspath(__file__))))
    raise ImportError("Failed to import mayaMenuBar modules: {}".format(str(e)))

def initialize():
    """Initialize the menu bar package."""
    try:
        ui.create_menu()
    except Exception as e:
        import traceback
        traceback.print_exc()
        raise RuntimeError("Failed to initialize menu: {}".format(str(e)))
